import '../CharacterSelector.css';
import {eyesData} from '../eyes.js';
import {lipsData} from '../lips.js';
import React, { useState } from 'react';


const CharacterSelector = ({onClickEyes, onClickLips}) => {
    const [currentIndexEyes, setCurrentIndexEyes] = useState(0);

    const handlerEyes = (eyesId) => {
        if (currentIndex < eyesData.length - 1) {
            onClickEyes(eyesData[currentIndexEyes].url);
            setCurrentIndexEyes(currentIndexEyes + 1);
          } else {
            onClickLips(lipsData[currentIndexEyes - eyesData.length].url);
            setCurrentIndexEyes(0);
          }
    }
    const handlerLips = (lipsId) => {
        onClickLips(lipsId);
    }

    return (
    <section className="selectorWrapper">
            <div className='buttonWrapper'>
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerEyes()}
                    >
                   Eyes
                    </button>
            </div>
            <div className='buttonWrapper'>
            {lipsData.map((lips) => (
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerLips(lips.url)}
                    >
                    {lips.name}
                    </button>
                ))}
            </div>
    </section>
    )
}

export { CharacterSelector };
