import '../CharacterSelector.css';
import {eyesData} from '../eyes.js';
import {lipsData} from '../lips.js';
import React, { useState } from 'react';


const CharacterSelector = ({onClickEyes, onClickLips}) => {
    const [currentIndexEyes, setCurrentIndexEyes] = useState(1);
    const [currentIndexLips, setCurrentIndexLips] = useState(1);

    const handlerEyes = () => {
        if (currentIndexEyes < eyesData.length) {
            onClickEyes(eyesData[currentIndexEyes].url);
            setCurrentIndexEyes(currentIndexEyes + 1);
          } else {
            onClickEyes(lipsData[currentIndexEyes - eyesData.length].url);
            setCurrentIndexEyes(0);
          }
    }


    const handlerLips = () => {
        if (currentIndexLips < lipsData.length - 1) {
            onClickLips(lipsData[currentIndexLips].url);
            setCurrentIndexLips(currentIndexLips + 1);
          } else {
            onClickLips(lipsData[currentIndexLips - lipsData.length].url);
            setCurrentIndexLips(0);
          }
    }

    return (
    <section className="selectorWrapper">
            <div className='buttonWrapper'>
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerEyes()}
                    >
                   Eyes
                    </button>
            </div>
            <div className='buttonWrapper'>
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerLips()}
                    >
                    Lips
                    </button>
            </div>
    </section>
    )
}

export { CharacterSelector };
