import React, { useRef, useState } from 'react';

import {eyesData} from '../eyes.js';
import {lipsData} from '../lips.js';
import {handsData} from '../hands.js';
import {decalData} from '../decal.js';
import {hairData} from '../hair.js';
import html2canvas from 'html2canvas'; 
import {CharacterPart} from './CharacterPart';

const CharacterCanvas = () => {
  const characterPreviewRef = useRef(null);
  const [downloadFormat, setDownloadFormat] = useState('jpg');

  const downloadImage = async () => {
    try {
      const characterPreviewRef = characterPreviewRef.current;
      const canvas = await html2canvas(CharacterPart, {
        scale: 2, 
        backgroundColor: downloadFormat === 'jpg' ? null : 'transparent', 
      });

      const link = document.createElement('a');
      link.download = `character.${downloadFormat}`;
      link.href = downloadFormat === 'jpg' ? canvas.toDataURL('image/jpeg') : canvas.toDataURL();
      link.click();
    } catch (error) {
      console.error('Ошибка при сохранении изображения', error);
    }
  };

  return (
    <div>
      {/* Код остальных компонентов и кнопок */}
      <button onClick={downloadImage}>Скачать изображение</button>
    </div>
  );
};

export {CharacterCanvas};
