import './App.css';
import React, { useState, useRef } from 'react';
import {eyesData} from './eyes.js';
import {lipsData} from './lips.js';
import {handsData} from './hands.js';
import {decalData} from './decal.js';
import {hairData} from './hair.js';
import {shirtData} from './shirt.js';
import {bgData} from './bg.js';

const App = ({ CharacterPart,  CharacterSelector, CharacterCanvas, CharacterGenerator}) => {

const characterPartRef = useRef(null);

const [eyes, setEyes] = React.useState(eyesData[2].url);
const [lips, setLips] = React.useState(lipsData[8].url);
const [hands, setHands] = React.useState(handsData[2].url);
const [decal, setDecal] = React.useState(decalData[8].url);
const [hair, setHair] = React.useState(hairData[8].url);
const [shirt, setShirt] = React.useState(shirtData[0].url);

const onClickEyes = (item) => {
  setEyes(item)
}
const onClickLips = (item) => {
  setLips(item)
}
const onClickHands = (item) => {
  setHands(item)
}
const onClickDecal = (item) => {
  setDecal(item)
}
const onClickHair = (item) => {
  setHair(item)
}
const onClickShirt = (item) => {
  setShirt(item)
}

  return (
    <main className="mainWrapper">
      <div class="characterWrapper">
        <div>
        <CharacterPart eyes = {eyes} lips = {lips} hands = {hands} decal = {decal} hair = {hair} shirt = {shirt} ref={characterPartRef}/>
        </div>
        <div className="mainSelectorWrapper">
        <CharacterSelector eyesData = {eyesData} onClickEyes = {onClickEyes} onClickLips = {onClickLips} onClickHands = {onClickHands} onClickDecal = {onClickDecal} onClickHair = {onClickHair} onClickShirt = {onClickShirt}/>
        </div>
        <div className="mainSelectorWrapper">
        <CharacterCanvas characterPartRef={characterPartRef}/>
        <CharacterGenerator onClickEyes = {onClickEyes} onClickLips = {onClickLips} onClickHands = {onClickHands} onClickDecal = {onClickDecal} />
        </div>
      </div>
    </main>
  );
}

export default App;
