const createHandlerNext = (data, currentIndex, setCurrentIndex, onClick, label) => () => {
  let newIndex = currentIndex + 1;
  if (newIndex >= data.length) {
      newIndex = 0;
  }
  setCurrentIndex(newIndex);
  onClick(data[newIndex].url);
};

const createHandlerPrev = (data, currentIndex, setCurrentIndex, onClick, label) => () => {
  let newIndex = currentIndex - 1;
  if (newIndex >= 0) {
      newIndex = data.length;
  }
  setCurrentIndex(newIndex);
  onClick(data[newIndex].url);
};

export {createHandlerNext, createHandlerPrev};
