import '../CharacterSelector.css';
import {eyesData} from '../eyes.js';
import {lipsData} from '../lips.js';


const CharacterSelector = ({onClickEyes, onClickLips}) => {


    const handlerEyes = (eyesId) => {
        if (currentIndex < eyesData.length - 1) {
            onClickEyes(eyesData[currentIndex].url);
            setCurrentIndex(currentIndex + 1);
          } else {
            onClickLips(lipsData[currentIndex - eyesData.length].url);
            setCurrentIndex(0);
          }
    }
    const handlerLips = (lipsId) => {
        onClickLips(lipsId);
    }

    return (
    <section className="selectorWrapper">
            <div className='buttonWrapper'>
                {eyesData.map((eyes) => (
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerEyes(eyes.url)}
                    >
                    {eyes.name}
                    </button>
                ))}
            </div>
            <div className='buttonWrapper'>
            {lipsData.map((lips) => (
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerLips(lips.url)}
                    >
                    {lips.name}
                    </button>
                ))}
            </div>
    </section>
    )
}

export { CharacterSelector };
