import React, { useState } from 'react';
import {eyesData} from '../eyes.js';
import {lipsData} from '../lips.js';
import {handsData} from '../hands.js';
import {decalData} from '../decal.js';
import '../CharacterSelector.css';


const CharacterGenerator = ({ onClickEyes, onClickLips, onClickHands, onClickShirts }) => {

const randomGenerator = (arr) => {
  let random = Math.floor(Math.random() * arr.length);
  return arr[random]
}

const handlerGenerator = () => {
  let randomEyesIndex  = randomGenerator(eyesData);
  let randomLipsIndex  = randomGenerator(lipsData);
  let randomDecalIndex  = randomGenerator(decalData);
  let randomHandsIndex  = randomGenerator(handsData);
  const selectedEyes = eyesData[randomEyesIndex];
  const selectedLips = lipsData[randomLipsIndex];
  const selectedDecal = decalData[randomDecalIndex];
  const selectedHands = handsData[randomHandsIndex];
  onClickEyes(selectedEyes.url);
  onClickLips(selectedLips.url);
  onClickShirts(selectedDecal.url);
  onClickHands(selectedHands.url);
}

return (
  <div>
    <button className="buttonBox ninth" onClick={handlerGenerator}>Generate!</button>
  </div>
);

};



export {CharacterGenerator};
