import '../CharacterSelector.css';
import {eyesData} from '../eyes.js';
import {lipsData} from '../lips.js';
import {handsData} from '../hands.js';
import {decalData} from '../decal.js';
import {hairData} from '../hair.js';
import {shirtData} from '../shirt.js';
import {bgData} from '../bg.js';
import React, { useState } from 'react';


const CharacterSelector = ({onClickEyes, onClickLips, onClickHands, onClickDecal, onClickHair, onClickShirt}) => {
    const [currentIndexEyes, setCurrentIndexEyes] = useState(0);
    const [currentIndexLips, setCurrentIndexLips] = useState(0);
    const [currentIndexHands, setCurrentIndexHands] = useState(0);
    const [currentIndexDecal, setCurrentIndexDecal] = useState(0);
    const [currentIndexHair, setCurrentIndexHair] = useState(0);
    const [currentIndexShirt, setCurrentIndexShirt] = useState(0);
    const [currentIndexBg, setCurrentIndexBg] = useState(0);

    const handlerEyes = () => {
        let newIndex = currentIndexEyes + 1;
        if (newIndex >= eyesData.length) {
            newIndex = 0;
        }
        setCurrentIndexEyes(newIndex);
        onClickEyes(eyesData[newIndex].url);
    }


    const handlerLips = () => {
        let newIndex = currentIndexLips + 1;
        if (newIndex >= lipsData.length) {
            newIndex = 0;
        }
        setCurrentIndexLips(newIndex);
        onClickLips(lipsData[newIndex].url);
    }

    const handlerHands = () => {
        let newIndex = currentIndexHands + 1;
        if (newIndex >= handsData.length) {
            newIndex = 0;
        }
        setCurrentIndexHands(newIndex);
        onClickHands(handsData[newIndex].url);
    }

    const handlerDecal = () => {
        let newIndex = currentIndexDecal + 1;
        if (newIndex >= decalData.length) {
            newIndex = 0;
        }
        setCurrentIndexDecal(newIndex);
        onClickDecal(decalData[newIndex].url);
    }

    const handlerHair = () => {
        let newIndex = currentIndexHair + 1;
        if (newIndex >= hairData.length) {
            newIndex = 0;
        }
        setCurrentIndexHair(newIndex);
        onClickHair(hairData[newIndex].url);
    }

    const handlerShirt = () => {
        let newIndex = currentIndexShirt + 1;
        if (newIndex >= shirtData.length) {
            newIndex = 0;
        }
        setCurrentIndexShirt(newIndex);
        onClickShirt(shirtData[newIndex].url);
    }

    const handlerBg = () => {
        let newIndex = currentIndexBg + 1;
        if (newIndex >= bgData.length) {
            newIndex = 0;
        }
        setCurrentIndexBg(newIndex);
        onClickBg(bgData[newIndex].url);
    }

    return (
    <section className="selectorWrapper">
            <div className='buttonWrapper'>
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerEyes()}
                    >
                   Eyes
                    </button>
            </div>
            <div className='buttonWrapper'>
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerLips()}
                    >
                    Lips
                    </button>
            </div>
            <div className='buttonWrapper'>
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerHands()}
                    >
                    Hands
                    </button>
            </div>
            <div className='buttonWrapper'>
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerDecal()}
                    >
                    Shirt
                    </button>
            </div>
            <div className='buttonWrapper'>
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerHair()}
                    >
                    Hair
                    </button>
            </div>
            <div className='buttonWrapper'>
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerShirt()}
                    >
                    Shirt Color
                    </button>
            </div>
            <div className='buttonWrapper'>
                    <button
                        className="buttonBox ninth"
                        onClick={() => handlerBg()}
                    >
                    Background
                    </button>
            </div>
    </section>
    )
}

export { CharacterSelector };
